import React, { Component, useState, useEffect } from 'react';
import actions from '../api/index';
import triple from '../images/triple.jpeg';
import ptPic from '../images/bruceWPt.jpeg';
function Home(props) {
    return (
        <div>
            <header className="homeHeader2">
                {/* <p>Welcome to Encompass HealthCare!</p> */}
                <p>Michigan's Premier Infectious Disease Specialist & Wound Care Facility</p>
            </header>

            <div className="homeSection1">
            {/* need a new picture of dr. ruben with a patient, a hand shake or hug would be perfect. */}
            <img src={ptPic} alt="Dr. Ruben with a Patient" className="homeSection1Pic"/>
            <div id="texty1"><h4 className="homeSection1txt">We're committed to impeccable patient care.</h4></div>

            {/* <button className="patientButton">Patient Testimonials</button>     */}
            </div>
            <div className="homeSection2">
            <h2>About Us</h2>
            <p>At Encompass HealthCare & Wound Medicine we provide a complete portfolio of healing modalities, techniques, and professionals. We are open 7 days a week to accomodate our unique patient base. Encompass was also built to accommodate patients with disabilities. Our ground floor entry includes plenty of accessible parking along with automatic doors extra-wide hallways and other ADA compliant accomodtions. Finally, our friendly and competent staff set us even further apart from other practices. We work hard to make every patient's experience with us a positive one, so they can focus on their healing process!</p>
            <p></p>
            </div>
            <div className="homeSection3">
                <p>As Metro-Detroit's only full-service wound healing & infection facility, we're equipped with hosipital-quality modalities in a safe and welcoming outpatient setting. </p>

                <img src={triple} alt="Dr. Ruben, Liz, Kitty treating patient" className="homeSection1Pic"/>
                {/* <button className="serviceButton">View Our Services</button>     */}
            </div>
        </div>
    );
}

export default Home;

//<p>"Every aspect of the environment and care is superb!" -Roger</p>
//<p>"Their staff is very informative, friendly, and caring!" -Blair</p>
